export default class tsec2jwt {
  constructor(host) {
    this.host = host;
  }

  convert(consumerId, tsec, policy) {
    const headers = this.buildHeaders(consumerId, tsec, policy);
  }

  buildHeaders(consumerId, tsec, policy) {
    return {
      'x-consumer-id': consumerId,
      'x-tsec-token': tsec,
      'x-validation-policy': policy,
    };
  }
}
